package Basics;

public class circle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int r;
		float pi, area, circum;
		
		r=5;
		pi=3.14f;
		
		area=pi*r*r;
		circum=2*pi*r;
		
		System.out.println("Area of Circle:"+area);
		System.out.println("Circum of Circle:"+circum);
		
			
				

	}

}
